mapper.select:
/* Admin - adminLogin */
SELECT admin_id, admin_authority
FROM admin
WHERE admin_id = #{admin_id} AND admin_pw = #{admin_pw}

mapper.insert:
/* Admin - adminInsert */
INSERT INTO admin(admin_id, admin_pw, admin_name, admin_phone,  admin_email, admin_regdate, admin_authority)
VALUES(#{admin_id}, #{admin_pw}, #{admin_name}, #{admin_phone}, #{admin_email}, default, default)

mapper.select:
/* Admin - adminIdConfirm */
SELECT
    NVL((
        SELECT 1 FROM admin
        WHERE admin_id = #{admin_id}
    ),0) AS state
FROM dual

/* Admin - adminPwConfirm */
SELECT
    NVL((
        SELECT 1 FROM admin
        WHERE admin_id = #{admin_id} AND admin_pw = #{admin_pw}
    ),0) AS state
FROM dual

/* Admin - adminKeyConfirm */
SELECT
    NVL((
        SELECT 1 FROM admin_key
        WHERE admin_key = #{admin_key}
    ),0) AS state
FROM dual

mapper.update:
	/* Admin - adminKeyUpdate */
 	UPDATE admin_key
 	SET admin_key = #{admin_key}

mapper.select:
/* Admin - adminInfo */
SELECT admin_id, admin_name, admin_phone, admin_email, TO_CHAR(admin_regdate, 'YYYY-MM-DD HH24:MI:SS') as admin_regdate, admin_authority
FROM admin
WHERE admin_id = #{admin_id}

mapper.update:
	/* Admin - adminUpdate */
 	UPDATE admin
 	SET admin_name = #{admin_name}, 
 		admin_phone = #{admin_phone},
 		admin_email = #{admin_email}
 	WHERE admin_id = #{admin_id}

	/* Admin - adminPwUpdate */
 	UPDATE admin
 	SET admin_pw = #{admin_pw}
 	WHERE admin_id = #{admin_id}

mapper.delete:
/* Admin - adminDelete */
DELETE FROM admin
WHERE admin_id = #{admin_id}
