mapper.select:
SELECT member_no, member_id, member_name, member_nickname, point_total, member_email, member_addr, member_phone, member_status, NVL(TO_CHAR(member_leavedate,'YYYY-MM-DD'),'-') AS member_leavedate, TO_CHAR(member_regdate,'YYYY-MM-DD') AS member_regdate, pickmoney_total
FROM ( SELECT /*+ INDEX_DESC(member member_member_no_pk) */ rownum AS rnum, member_no, member.member_id AS member_id, member_name, member_nickname, member_email, member_addr, member_phone, member_status, member_leavedate, member_regdate, NVL(point_total,0) AS point_total, NVL(PICKMONEY_TOTAL,0) AS pickmoney_total FROM member LEFT JOIN point ON member.member_id = point.member_id LEFT JOIN pickmoney ON point.member_id = pickmoney.member_id
WHERE 1=1
rownum <= #{pageNum} * #{amount}
) memberlist
WHERE rnum > (#{pageNum} - 1) * #{amount}

mapper.update:
UPDATE member
SET member_status = 'N', member_leavedate = SYSDATE
WHERE member_id=#{member_id}

mapper.select:

SELECT MEMBER_ID,MEMBER_NAME,MEMBER_PHONE
FROM member 
WHERE MEMBER_ID = #{member_id} AND MEMBER_NAME = #{member_name} AND MEMBER_PHONE=#{member_phone}


SELECT MEMBER_ID,MEMBER_NAME,MEMBER_EMAIL
FROM member 
WHERE MEMBER_ID = #{member_id} AND MEMBER_NAME = #{member_name} AND MEMBER_EMAIL=#{member_email}

mapper.update:

UPDATE member
SET member_pw=#{member_pw}
WHERE member_id=#{member_id}

mapper.select:

SELECT member_id,member_pw
FROM member
where member_id=#{member_id} AND member_pw=#{member_pw}

mapper.update:

UPDATE member
SET MEMBER_STATUS='N'
where member_id=#{member_id} AND member_pw=#{member_pw}
